import React from 'react';
import {GoogleLogin,GoogleLogout} from 'react-google-login';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import { createMuiTheme } from '@material-ui/core/styles';
import white from '@material-ui/core/colors/white';

import {loginWithToken, sampleCall} from './client';

const theme = createMuiTheme({
  palette: {
    primary:{
      main: '#FFFFFF',
    },
  },
});


class LoginComp extends React.Component{



	constructor(props){
		super(props)
		this.state = {loggedIn: false};
	}

	render() {

		var sampleCallButton;
		if(this.state.loggedIn){
			sampleCallButton = 
			<Grid item xs={12}>
				<Button variant="outlined" color="primary"
					onClick={sampleCall}>Sample Call</Button>
			</Grid>
		}

		return (
			<div>
			<Grid container spacing={3}>
					{sampleCallButton}
				<Grid item xs={12}>
					<GoogleLogin
						clientId="436041114033-g9veoeqo9cvqlvia27t30oibijah35mf.apps.googleusercontent.com"
						buttonText="Login with Google"
						onSuccess={res => this.responseGoogle(res)}
						isSignedIn={true}
						/>
				</Grid>
			</Grid>			
			</div>);
	}

	responseGoogle(res) {
		loginWithToken(res.tokenId, () =>{
			this.setState((state) => {
				return {loggedIn: true};
			});

		})
	}

	sampleCall() {
		sampleCall();	
	}

}



export default LoginComp;
