import React from 'react';
import {GoogleLogin,GoogleLogout} from 'react-google-login';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import { withCookies, Cookies } from 'react-cookie';
import {loginWithToken, sampleCall} from '../../client';

class SSOLoginComp extends React.Component{

	 static propTypes = {
	    cookies: instanceOf(Cookies).isRequired
	  };


	constructor(props){
		super(props)
		 const { cookies } = props;
		this.state = {
			loggedIn: false,
			jwtToken: cookies.get('jwtToken')
		};
	}

	render() {

		var sampleCallButton;
		if(this.state.loggedIn){
			sampleCallButton = 
			<Grid item xs={12}>
				<Button variant="outlined" color="primary"
					onClick={sampleCall}>Sample Call</Button>
			</Grid>
		}

		return (

			<div>
			<Grid container spacing={3}>
					{sampleCallButton}
				<Grid item xs={12}>
					<GoogleLogin
						clientId="436041114033-g9veoeqo9cvqlvia27t30oibijah35mf.apps.googleusercontent.com"
						buttonText="Login with Google"
						onSuccess={this.responseGoogle}
						isSignedIn={true}
						/>
				</Grid>
			</Grid>			
			</div>);
	}

	responseGoogle(res) {
		const { cookies } = this.props;
		console.log("repsonse google")
		loginWithToken(res.tokenId, (token) =>{
			const [setCookie] = useCookies(['jwtToken']);
			cookies.set('jwtToken', token, { secure: true, httpOnly: true, sameSite:true });
			this.setState((state) => {
				return {loggedIn: true};
			});

		})
	}

	sampleCall() {
		const [cookies] = useCookies(['jwtToken']);
		sampleCall(cookies.jwtToken);	
	}

}

export default SSOLoginComp;
